import React, { useState } from 'react';
import SearchForm from './components/SearchForm';
import MealCard from './components/MealCard';
import API from './api';

export default function App(){
  const [meals, setMeals] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const search = async (params) => {
    setLoading(true); setError('');
    try {
      const res = await API.get('/meals', { params });
      setMeals(res.data);
    } catch (err) {
      console.error(err); setError('Failed to fetch meals');
    } finally { setLoading(false); }
  };

  return (
    <div className="container">
      <nav className="navbar navbar-light bg-white mb-4 shadow-sm">
        <div className="container-fluid">
          <span className="navbar-brand mb-0 h1">üçΩÔ∏è Meal Planner</span>
          <small className="text-muted">Suggests meal prep ideas for your budget & ingredients</small>
        </div>
      </nav>

      <div className="row">
        <div className="col-md-4">
          <div className="card p-3 mb-3 shadow-sm">
            <h5>Find Meals</h5>
            <SearchForm onSearch={search} />
          </div>
          <div className="card p-3 shadow-sm">
            <h6>Tips</h6>
            <ul>
              <li>Provide ingredients you already have for best matches.</li>
              <li>Use budget filter to find cheaper or premium recipes.</li>
              <li>Mix and match recipes for meal prep.</li>
            </ul>
          </div>
        </div>

        <div className="col-md-8">
          <div className="card p-3 shadow-sm">
            <h5 className="mb-3">Suggested Meals</h5>
            {loading && <p>Loading...</p>}
            {error && <div className="alert alert-danger">{error}</div>}
            {meals.length === 0 && !loading && <p className="text-muted">No meals yet. Use the form to search.</p>}
            <div className="card-grid mt-3">
              {meals.map(m => <MealCard key={m.id} meal={m} />)}
            </div>
          </div>
        </div>
      </div>

      <footer className="text-center text-muted mt-4">
        <small>¬© {new Date().getFullYear()} Meal Planner</small>
      </footer>
    </div>
  );
}
